$("#online-registration").validate({
	rules: {
	    fullName: {
	      required: true
	    },
	    email:{
	    	required:true,email:true
	    },
	    contactNumber:{
	    	required:true
	    },
	    address:{
	    	required:true
	    },
	    city:{
	    	required:true
	    },
	    state:{
	    	required:true
	    },
	    country:{
	    	required:true
	    },
	    zipcode:{
	    	required:true
	    },
	    course:{
	    	required:true
	    },
	    branch:{
	    	required:true
	    },
	    amount:{
	    	required:true
	    }, answer:{required:true}
  },
	submitHandler:function(form){
	    var ans=$("#answer").val();
	    var pre=$("#answer").attr("pattern");
	    if(ans==pre){
	        form.submit();
	    }else{
	        $("#answer").next("label.error").remove();
	        $("#answer").next("<label class='error'>please fill valid captch</label>")
	    }
	}
});

